load_dotenv()
web3 = Web3(Web3.HTTPProvider(os.environ['INFURA_URL']))

def checkaddress(account):
    return web3.isChecksumAddress(account)
# application binary interface
#1eth = 1000000000wei
def getbalanceeth(account):
    balance =web3.eth.get_balance(web3.toChecksumAddress(account))
    return web3.fromWei(balance,'ether')

